<template>
  <div id="blank-view" :class="className">
    <div class="bg"></div>
    <div class="msg" v-html="msg"></div>
  </div>
</template>

<script>
export default {
  props: {
    type: {
      default: 0,
      type: [String, Number],
    },
  },
  computed: {
    msg() {
      let relativeArr = ['暂无相关信息', '权限不足，<br>暂无法查看更多信息'];

      return relativeArr[parseInt(isNaN(this.type) ? 0 : this.type)];
    },
    className() {
      let num = parseInt(isNaN(this.type) ? 0 : this.type);

      if (num === 1) return 'noPower';
      return '';
    },
  },
};
</script>

<style lang="less">
#blank-view {
  position: absolute;
  top: 50%;
  left: 50%;
  width: 2.24rem;
  margin: 0 auto;
  transform: translate(-50%, -50%);

  &.noPower {
    width: 3.2rem;

    .bg {
      width: 3.2rem;
      height: 3.2rem;
      margin-bottom: 0.1rem;
      background-image: url(data:image/png;base64,);
    }

    .msg {
      font-size: 0.24rem;
